package Bai1;

public class Toado {

		private int X;
		private int Y;
		
		
		public Toado(int x, int y) {
			super();
			this.X = x;
			this.Y = y;
			
			
		}
		public Toado() {
			super();
			// TODO Auto-generated constructor stub
		}
		public int getX() {
			return X;
		}
		public void setX(int x) {
			this.X = x;
		}
		public int getY() {
			return Y;
		}
		public void setY(int y) {
			this.Y = y;
		}
		@Override
		public String toString() {
			return "Toado [X=" + X + ", Y=" + Y + "]";
		}
		
		
		
		
		

	}

